import React, { useState, useEffect } from 'react';
import {getAllPosts, getById} from "../../Common/Services/LearnService";
import {getBlogById} from "../../Common/Services/BlogService";
  
  const PostsList = (blog) => {
      // Variables in the state to hold data
      const [posts, setPosts] = useState([]);
      const [post, setPost] = useState([]);
      const [postTitle, setPostTitle] = useState();
    
      // UseEffect to run when the page loads to
      // obtain async data and render
      useEffect(() => {
        getAllPosts().then((posts) => {
          setPosts(posts);
        });
      }, []);

    let blogObject = getBlogById(blog);

    // posts are funky but there is a reason
    // by having dict rather than array, searching for a specific post # is much faster
    posts.map(
      (post) =>
      {
        let blogTemp = getBlogById(post.get("blog"));
        let blogPosts = blogTemp.get("posts");
        blogPosts.append(post);
        blogTemp.set("posts", blogPosts);
      }
    )
    var blogPostsIds = blogObject.get("posts");
    var blogPosts = [];
    blogPostsIds.map((postId) => { blogPosts.append(getById(postId));});
    return (
      <div class="collapse" id={blogObject.id}>
        {blogPosts.map(
          (post) =>
          (
              <div class="card card-body">
                {post.get("title")} | {post.get("user").get("username")} | {post.get("updated_at")}
              </div>
          )
        )}
      </div>
    );
  }

  export {PostsList}
