import React, { useState, useEffect } from 'react';
import {getAllPosts, getById} from "../../Common/Services/LearnService";
import {getBlogById} from "../../Common/Services/BlogService";
  
  const PostsList = (blog) => {
      // Variables in the state to hold data
      const [posts, setPosts] = useState([]);
      const [post, setPost] = useState([]);
      const [postTitle, setPostTitle] = useState();
    
      // UseEffect to run when the page loads to
      // obtain async data and render
      useEffect(() => {
        getAllPosts().then((posts) => {
          setPosts(posts);
        });
      }, []);

    var blog = blog["blog"];
    console.log(blog);

    var blogPosts = [];
    posts.map(
      (post) =>
      {
        if(post.get("blog") == blog)
        {
          blogPosts.append(post);
        }
      }
    )
    return (
      <div class="collapse" id={blog}>
        {blogPosts.map(
          (post) =>
          (
              <div class="card card-body">
                {post.get("title")} | {post.get("user").get("username")} | {post.get("updated_at")}
              </div>
          )
        )}
      </div>
    );
  }

  export {PostsList}
