import React from 'react';
  // make it so that this gets the posts from the blog
  // separate function becuase maybe it can be prettier later
  function getPosts(blog) 
  {
    return blog["data"]["pages"];
  }
  
  // extract the posts from the json
  export function outputPosts(blog) 
  {
    // every blog div needs a unique id
    var collapseName = blog["id"];
    // posts are funky but there is a reason
    // by having dict rather than array, searching for a specific post # is much faster
    var posts = getPosts(blog);
    var postNumbers = Object.keys(posts);
    // get and display posts by post number
    return (
      postNumbers.map(
        (postNum) =>
        {
          let post = posts[postNum];
          return (
          <div class="collapse" id="{collapseName}">
            <div class="card card-body">
              {post.title} | {post.user} | {post.posted_date}
            </div>
          </div>)
        },
      )
    );
  }
