import React from 'react';
// this file is the framework for displaying the list of blogs

// function for opening a blog container
// allows collapse elements to function on click even when not in default bootstrap format
// for use later
/*
export function collapseToggle(id) 
{
  let collapsible = id+"Collapse";
  $(collapsible).collapse({toggle: true});
  console.log("toggled "+collapsible);
}
*/

// exports blogs of a certain type, called "title" for code consistency
// renders them as a card, where the type is the header
// and when clicked, it expands into the list of blogs
const Blogs = ({ blogTitle, children, onChangeBlogTitle, onClick }) => {
  return (
      <div>
      <hr />
      <form>
        <p>Blog Title</p>
        <input text="blogTitle" onChange={onChangeBlogTitle} />
        <button type="submit" onChange={onClick}>
          Submit Blog
        </button>
      </form>
    </div>

  );
};

export { Blogs } ;
