import Parse from "parse";
/* SERVICE FOR PARSE SERVER OPERATIONS - BLOGS */

/* ADAPTED FROM LECTURE 14 EXAMPLE */

// CREATE operation - new blog with blogTitle
export const createBlog = (BlogTitle, ) => {
  console.log("Creating: ", BlogTitle);
  const Blog = Parse.Object.extend("Blog");
  const blog = new Blog();
  // using setter to UPDATE the object
  blog.set("blogTitle", BlogTitle);
  return blog.save().then((result) => {
    // returns new Blog object
    return result;
  });
};

// READ operation - get blog by ID
export const getBlogById = (id) => {
  const Blog = Parse.Object.extend("Blog");
  const query = new Parse.Query(Blog);
  return query.get(id).then((result) => {
    // return Blog object with objectId: id
    return result;
  });
};

// READ operation - get all blogs in Parse class Blog
export const getAllBlogs = () => {
  const Blog = Parse.Object.extend("Blog");
  const query = new Parse.Query(Blog);
  return query.find().then((results) => {
    // returns array of Blog objects
    return results;
  });
};

// DELETE operation - remove blog by ID
export const removeBlog = (id) => {
  const Blog = Parse.Object.extend("Blog");
  const query = new Parse.Query(Blog);
  return query.get(id).then((blog) => {
    blog.destroy();
  });
};
